import react from 'react'
import Link from 'next/link'

import bcStyles from './styles/breadcrumbs.module.scss'

const BreadCrumbs = (props) => {
    let rootPath = '/';
    return (
        <div className={bcStyles.bcContainer}>
            <p className={bcStyles.breadcrumb}>
                {props.slugs.map((item, idx) => {
                    let path = item.toLowerCase();
                    rootPath += { item }
                    return (
                        <react.Fragment>
                            {idx === props.slugs.length - 1 ? (
                                <span className={bcStyles.active}>{item}</span >
                            ) :
                                (
                                    <react.Fragment>
                                        <Link key={idx} href={`/${item.toLowerCase()}`}><a href={`/${item.toLowerCase()}`} className={bcStyles.links}>{item}</a></Link>{' '}> {' '}
                                    </react.Fragment>
                                )
                            }
                        </react.Fragment>
                    )
                })
                }
            </p>
        </div >
    )
}

export default BreadCrumbs;


