import React from 'react'
//import layout
import FitnessLayout from '../../components/layouts/FitnessLayout'
import ContentContainer from '../../components/layouts/ContentContainer'
import ContentFragment from '../../components/layouts/ContentFragment'
import cx from 'classnames'

// import components
import WorkoutContainer from '../../components/fitness/WorkoutContainer'
import WorkoutItem from '../../components/fitness/WorkoutItem'
import Disclaimer from '../../components/shared/disclaimer'
import WorkoutNotation from '../../components/fitness/WorkoutNotation'
import SuggestPages from '../../components/shared/SuggestPages'

//import data
import data from '../../public/static/data/FitnessItems/dietGeneral'
import disc from '../../public/static/data/FitnessItems/disclaimer'

import Fade from 'react-reveal/Fade';

class GeneralDiet extends React.Component {
    render() {
        const headerData = data.header;
        const keyIdeaData = data.keyidea;
        const calorieData = data.calorie;
        const macroData = data.macro;
        const portionData = data.portion; //food pyramid
        const avoidData = data.avoid; //food pyramid
        return (
            <FitnessLayout>
                <ContentContainer breadcrumbs={['Fitness', 'generalDiet']}>
                    <Disclaimer text={disc.disclaimer} />
                    {/* Insert Fragments here */}
                    <ContentFragment title={headerData.title} text={headerData.text}>
                        {/* no photos */}
                    </ContentFragment>
                    <ContentFragment subtitle={keyIdeaData.title} text={keyIdeaData.text}>
                        {/* no photos */}
                    </ContentFragment>

                    {/* How much to eat */}
                    <ContentFragment
                        title={calorieData.title} text={calorieData.text}
                        link={calorieData.link} linkText="Read More"
                    >

                        {/* foods */}
                        <div className="row">
                            <Fade bottom distance='20px'>
                                <WorkoutContainer
                                    title="Mains"
                                    itemsPerRow={3}
                                    theme="blue"
                                >
                                    <WorkoutItem name="White Rice (200g)" sets="300kcal" />
                                    <WorkoutItem name="Brown Rice (200g)" sets="222kcal" />
                                    <WorkoutItem name="Nasi Lemak (1 Plate)" sets="400kcal" />
                                    <WorkoutItem name="Fried Bihun (1 Plate)" sets="300kcal" />
                                    <WorkoutItem name="Fried Rice (200g)" sets="330kcal" />
                                    <WorkoutItem name="Hainanese Chicken Rice (200g)" sets="345kcal" />
                                    <WorkoutItem name="Fried Kuey Teow (200g)" sets="304kcal" />
                                    <WorkoutItem name="Oats (30g)" sets="100kcal" />
                                </WorkoutContainer>
                            </Fade>
                            <Fade bottom distance='20px' delay={200}>
                                <WorkoutContainer
                                    title="Bread-Based"
                                    itemsPerRow={3}
                                    theme="green"
                                >
                                    <WorkoutItem name="Roti Canai (each)" sets="300kcal" />
                                    <WorkoutItem name="Roti Telur (each)" sets="415kcal" />
                                    <WorkoutItem name="Tosai (each)" sets="196kcal" />
                                    <WorkoutItem name="Chapatti (each)" sets="60kcal" />
                                    <WorkoutItem name="Mexican Bun (each)" sets="460kcal" />
                                    <WorkoutItem name="Double Cheeseburger (each)" sets="300kcal" />
                                </WorkoutContainer>
                            </Fade>
                            <Fade bottom distance='20px' delay={200}>
                                <WorkoutContainer
                                    title="Beverages"
                                    itemsPerRow={3}
                                    theme="purple"
                                >
                                    <WorkoutItem name="Teh Tarik (1 serving)" sets="100kcal" />
                                    <WorkoutItem name="Milo (1 serving)" sets="120kcal" />
                                    <WorkoutItem name="Americano (16oz)" sets="15kcal" />
                                    <WorkoutItem name="Latte (16oz)" sets="220kcal" />
                                    <WorkoutItem name="Cappucino (16oz)" sets="100kcal" />
                                    <WorkoutItem name="Hot Chocolate (1 serving)" sets="130kcal" />
                                    <WorkoutItem name="Tea (1 serving)" sets="5kcal" />
                                    <WorkoutItem name="Green Tea (1 serving)" sets="3kcal" />
                                    <WorkoutItem name="Bobba Tea (1 serving)" sets="150-250kcal" />
                                </WorkoutContainer>
                            </Fade>
                        </div>

                        {/* Photos
                        <div className="row">
                            <Fade bottom distance='20px'>
                                <div className={cx("rowPhotoPortrait", "intro1")}></div>
                            </Fade>
                            <Fade bottom distance='20px' delay={200}>
                                <div className={cx("rowPhotoPortrait", "intro2")}></div>
                            </Fade>
                            <Fade bottom distance='20px' delay={400}>
                                <div className={cx("rowPhotoLandscape", "intro3")}></div>
                            </Fade>
                        </div> */}
                    </ContentFragment>

                    <SuggestPages moreOn="Fitness" links={[
                        {
                            link: '/fitness/iifym',
                            text: 'The IIFYM Diet'
                        },
                        {
                            link: '/fitness/hypertrophy',
                            text: 'Hypertrophy Programs'
                        },
                        {
                            link: '/fitness/fatloss',
                            text: 'Fat Loss Programs'
                        },
                        {
                            link: '/fitness/transformation',
                            text: 'My Transformation'
                        }
                    ]} />
                </ContentContainer>
                <style jsx>{`
                .intro1 {
                    background-image: url('/static/images/fitness/achievements/transformation/skinny2.jpeg');
                }

                .intro2 {
                    background-image: url('/static/images/fitness/achievements/transformation/skinny3.jpeg');
                }

                .intro3 {
                    background-image: url('/static/images/fitness/achievements/transformation/skinny1.jpg');
                }

                .bulk1 {
                    background-image: url('/static/images/fitness/achievements/transformation/bulk1.jpg');
                }

                .bulk2{
                    background-image: url('/static/images/fitness/achievements/transformation/bulk2.jpg');
                }
                .bulk3{
                    background-image: url('/static/images/fitness/achievements/transformation/bulk3.jpg');
                }

                .transform1{
                    background-image: url('/static/images/fitness/achievements/transformation/transform1.jpg');
                }
                .transform2{
                    background-image: url('/static/images/fitness/achievements/transformation/transform2.jpg');
                }
                .transform3{
                    background-image: url('/static/images/fitness/achievements/transformation/transform3.jpg');
                }
                .transform4{
                    background-image: url('/static/images/fitness/achievements/transformation/transform4.jpg');
                }
                .transform5{
                    background-image: url('/static/images/fitness/achievements/transformation/transform5.jpg');
                }
                .transform6{
                    background-image: url('/static/images/fitness/achievements/transformation/transform6.jpg');
                }
                .transform7{
                    background-image: url('/static/images/fitness/achievements/transformation/transform7.jpg');
                }
                .transform8{
                    background-image: url('/static/images/fitness/achievements/transformation/transform8.jpg');
                }
                .maintain1{
                    background-image: url('/static/images/fitness/achievements/transformation/maintenance1.jpg');
                }
                .maintain2{
                    background-image: url('/static/images/fitness/achievements/transformation/maintenance2.jpeg');
                }
                .maintain3{
                    background-image: url('/static/images/fitness/achievements/transformation/maintenance3.jpg');
                }
                .maintain4{
                    background-image: url('/static/images/fitness/achievements/transformation/maintenance4.jpg');
                }

            `}</style>
            </FitnessLayout>
        )
    }
}

export default GeneralDiet
