import react from 'react'
import VisibilitySensor from 'react-visibility-sensor'

class ContentContainer extends react.Component {


    render() {
        return (
            <VisibilitySensor>
                {({ isVisible }) =>
                    <div className='contentContainer'>
                        {props.children}
                    </div>
                }
            </VisibilitySensor >
        )
    }
}

export default contentContainer;
