import react from 'react'

import tabsStyles from './styles/tabs.module.scss'

class Tabs extends react.Component {
    constructor() {
        super();

        this.state = {
            academicClicked: true,
            proClicked: false,
            personalClicked: false
        }

        this.handleClick = this.handleClick.bind(this);
    }

    handleClick(item) {
        let newState = {}
        Object.keys(this.state).map((key, value) => {
            if (key === `${item}Clicked`) {
                newState[`${key}`] = true;
            }
            else {
                newState[`${key}`] = false;
            }
        })
        this.setState(newState);
    }

    render() {
        console.log(this.state)
        return (
            <div className={tabsStyles.tabContainer}>
                {this.props.children.map((child, idx) => {
                    return (
                        <a key={idx} href="#" onClick={() => this.handleClick(child.props.children)}>{child}</a>
                    )
                })
                }

            </ div >
        )
    }
}

export default Tabs;
