import react from 'react';
import Link from 'next/link';
import fetch from 'isomorphic-unfetch';
import cx from 'classnames';

// import layouts
import ContentLayout from '../components/layouts/ContentLayout'
import ContentContainer from '../components/layouts/ContentContainer'

//import components
import ContentHead from '../components/shared/ContentHead'

//global styles can be found at ../styles/main.scss
//import specific styling module
import blogsStyles from './styles/blogs.module.scss'

//React Reveal Animation
import Fade from 'react-reveal/Fade';

// import data
import contentHeadData from '../public/static/data/contentHeads.json'

class Blogs extends react.Component {

    static async getInitialProps() {
        let posts = [];

        try {
            const response = await fetch('https://jsonplaceholder.typicode.com/posts');
            posts = await response.json();
        } catch (err) {
            console.log(err);
        }

        return { posts: posts.splice(0, 10) };
    }

    renderPosts(posts) {
        return posts.map((post) => {
            return (
                // every iterated react item needs to have a key
                <li key={post.id}>
                    <Link as={`/blogs/${post.id}`} href="/blogs/[id]">
                        <a>{post.title}</a>
                    </Link>
                </li>
            )
        }
        )
    }

    render() {
        // console.log(this.props.posts);
        const posts = this.props.posts;

        return (
            <ContentLayout>
                <section>
                    <div className={cx('hero', blogsStyles.blogsHero)}></div>

                    <ContentContainer>
                        <Fade delay={200}>
                            <ContentHead data={contentHeadData.blogs} />
                        </Fade>
                    </ContentContainer>
                </section>
                <section>
                    <ContentContainer>
                        <div className={blogsStyles.blogsContainer}>
                            <div className={blogsStyles.blogItem}>
                                <a href="#">
                                    <p className={blogsStyles.title}>Why I Felt Like I Should Start Writing Blogs</p>
                                </a>

                                <p className={blogsStyles.author}>Author: <span className={blogsStyles.name}>Asyrul Hafetzy</span></p>
                                <p className={blogsStyles.date}>Published on: November 2019</p>
                                <p className={blogsStyles.text}>Contrary to popular belief, Lorem Ipsum is not simply random text. It has roots in a piece of classical Latin literature from 45 BC, making it over 2000 years old. Richard McClintock, a Latin professor at Hampden-Sydney College in Virginia, looked up one of the more obscure Latin words, consectetur, from a Lorem Ipsum passage, and going through the cites of the word in classical...<span className={blogsStyles.readmore}><a href="#">READ MORE</a></span></p>
                            </div>
                        </div>
                    </ContentContainer>
                </section>
            </ContentLayout>
        )
    }
}

export default Blogs;

