import React from 'react';
import Link from 'next/Link';
import cx from 'classnames'

// import layouts
import ContentLayout from '../components/layouts/ContentLayout'
import ContentContainer from '../components/layouts/ContentContainer'

//import components
import ContentHead from '../components/shared/ContentHead'
import Tabs from '../components/shared/Tabs'

//import data
import contentHeadData from '../public/static/data/contentHeads.json'

//global styles can be found at ../styles/main.scss
//import specific styling module
import projectsStyles from './styles/projects.module.scss';

//React Reveal Animation
import Fade from 'react-reveal/Fade';

class Projects extends React.Component {
    constructor() {
        super()
        this.state = {
            activeTab: '',
            activeProject: ''
        }
    }

    render() {
        console.log(this.state)
        return (
            <ContentLayout>
                <section>
                    <div className={cx('hero', projectsStyles.projectsHero)}></div>

                    <ContentContainer>
                        <Fade delay={200}>
                            <ContentHead data={contentHeadData.projects} />
                        </Fade>

                        <Tabs onChange={active => { this.setState({ activeTab: active }) }} active={this.state.activeTab}>
                            <span key="Academic">Academic</span>
                            <span key="Professional">Professional</span>
                            <span key="Personal">Personal</span>
                        </Tabs>

                        <ProjectsTab>
                            <span key="rpi">Using Raspberry Pi to Drive CNC Machine</span>
                            <span key="chatbot">A Chatbot to Facilitate Data Analysis</span>
                        </ProjectsTab>
                    </ContentContainer>
                </section>
            </ContentLayout >
        )
    }
}

export default Projects;
