import react from 'react'

import tabsStyles from './styles/tabs.module.scss'

class Tabs extends react.Component {
    constructor() {
        super();

        this.state = {
            AcademicActive: true,
            ProfessionalActive: false,
            PersonalActive: false
        }

        this.handleClick = this.handleClick.bind(this);
    }

    handleClick(item) {
        let newState = {}
        Object.keys(this.state).map((key, value) => {
            if (key === `${item}Active`) {
                newState[`${key}`] = true;
            }
            else {
                newState[`${key}`] = false;
            }
        })
        this.setState(newState);
    }

    render() {
        return (
            <div className={tabsStyles.tabContainer}>
                {this.props.children.map((child, idx) => {
                    return (
                        // <a key={idx} href="#" onClick={() => this.handleClick(child.key)}
                        //     className={this.state[`${child.key}Active`] ? tabsStyles.active : null}>
                        //     {child}
                        // </a>
                        <a key={idx} href="#" onClick={() => this.handleClick(child.key)}
                            className={this.state[`${child.key}Active`] ? tabsStyles.active : null}>
                            {child}
                        </a>
                    )
                })
                }
            </ div >
        )
    }
}

export default Tabs;
