// import specific style here
import react from 'react'
import skillbarStyles from './skillbar.module.scss';
import cx from 'classnames'
import VisibilitySensor from 'react-visibility-sensor'

class SkillBar extends React.Component {
    render() {
        return (
            <div className={skillbarStyles.skillBarContainer} >
                <div className={skillbarStyles.skill}><span>{props.skill}</span></div>
                <div className={skillbarStyles.statsContainer}>
                    <div className={cx(skillbarStyles.stats, 'statsAnimate')}></div>
                </div>
                <span className='span2'>{props.stats}%</span>
                <style jsx>{`
                    .statsAnimate {
                        animation: expand 1s ease-in;
                        animation-fill-mode: forwards;
                        animation-delay: 0.5s;
                    }

                    span{
                            animation: fadeIn 0.5s ease-in forwards;
                            animation-delay: 0.5s;
                    }

                    .span2 {
                        animation-delay: 1s
                    }

                    @keyframes expand {
                        from {
                          width: 1%;
                        }
                        to {
                          width: ${props.stats}%;
                        }
                    }

                    @keyframes fadeIn {
                        from {
                          opacity: 0;
                        }
                        to {
                          opacity: 1;
                        }
                    }
                `}</style>
            </div>
        )
    }
}

export default SkillBar;

