import react from 'react'
import BaseLayout from './BaseLayout'
import BackToTop from '../shared/BackToTop'

class ContentLayout extends react.Component {
    constructor() {
        super();
        this.state = {
            documentHeight: 0,
            viewportHeight: 0
        }
    }

    componentDidMount() {
        // console.log(window.document.body.offsetHeight);
        this.setState({
            documentHeight: window.document.body.offsetHeight,
            viewportHeight: document.documentElement.clientHeight
        })
    }

    componentDidUpdate(prevProps, prevState, snapshot) {
        // console.log('Prev Doc height: ', this.state.documentHeight);
        // console.log('New Doc height: ', window.document.body.offsetHeight);
        console.log('New Doc height: ', this.state.documentHeight);
        if (this.state.documentHeight !== prevState.documentHeight) {
            console.log('goes');

            this.setState({
                documentHeight: window.document.body.offsetHeight
            })
        }
    }

    render() {
        const padding = 500;
        // console.log('Doc height: ', this.state.documentHeight);
        // console.log('Viewport height: ', this.state.viewportHeight);
        // console.log(`Viewport height + ${padding}: `, this.state.viewportHeight * 2.5);

        return (
            <BaseLayout>
                {this.props.children}

                {this.state.documentHeight > this.state.viewportHeight * 2 &&
                    <BackToTop></BackToTop>
                }
            </BaseLayout>
        )
    }
}

export default ContentLayout;
