import react from 'react';
import bloglistStyles from './blogslist.module.scss'
import Link from 'next/link'

const BlogsList = (props) => {
    return (
        <react.Fragment>
            {/* {props.data.map((item, idx) => { */}
            {props.posts.map((item, idx) => {
                let itemText = item.content.replace(/(<([^>]+)>)/ig, '');
                itemText = itemText.slice(0, 500);
                return (
                    <div key={item.ID} className={bloglistStyles.blogItem}>
                        <Link as={`/blogs/${item.slug}`} href="/blogs/[pid].js">
                            <a href="#">
                                <p className={bloglistStyles.title}>{item.title}</p>
                            </a>
                        </Link>
                        <p className={bloglistStyles.author}>Author: <span className={bloglistStyles.name}>{item.author.nice_name}</span></p>
                        <p className={bloglistStyles.date}>Published on: {item.date}</p>
                        <p className={bloglistStyles.text}>{itemText}...
                            <span className={bloglistStyles.readmore}>
                                <Link as={`/blogs/${item.slug}`} href="/blogs/[pid].js">
                                    <a href="#">READ MORE</a>
                                </Link>
                            </span>
                        </p>
                    </div>
                )
            })}
        </react.Fragment>
    )
}

export default BlogsList;
