import react from 'react';
import Link from 'next/Link';
import BaseLayout from '../components/layouts/BaseLayout'

//global styles can be found at ../styles/main.scss
//import specific styling module
import indexStyles from './styles/index.module.scss';

// this is the landing page

class Index extends react.Component {

    constructor() {
        // calling super constructor
        super();

        // set state
        this.state = {
            title: 'I am index page'
        }

        //binding local methods
        this.updateTitle = this.updateTitle.bind(this);
    }

    // react lifecycle methods
    // 1. componentDidMount()
    // 2. componentDidUpdate()
    // 3. componentWillUnmount()
    // 4. getInitialProps - Next specialty

    // local method
    updateTitle() {
        this.setState({
            title: 'I am updated Title'
        })
    }


    render() {
        return (
            < BaseLayout >
                <div className={indexStyles.IndexHero}>
                    <div className={indexStyles.textContainer}>
                        <div>
                            <h4 className={indexStyles.Welcome}>Welcome to My World</h4>
                        </div>
                        <div>
                            <h4 className={indexStyles.Name}>Asyrul Hafetzy Ahmad</h4>
                            <h4 className={indexStyles.Job}>Full-Stack Web Developer</h4>
                        </div>
                    </div>
                </div>
            </ BaseLayout >
        )
    }
}

export default Index;

