import react from 'react'

//import layout
import ContentFragment from '../layouts/ContentFragment'
import cx from 'classnames'

const ProjectRpi = (props) => {
    const project = props.data
    return (
        <react.Fragment>
            <ContentFragment title={project.title}
                time={project.time} text={project.intro}>

                <div className="row">
                    <div className={cx("rowPhotoPortrait", "intro1")}></div>
                    <div className={cx("rowPhotoLandscape", "intro2")}></div>
                    <div className={cx("rowPhotoPortrait", "intro1")}></div>
                </div>
            </ContentFragment>

            <ContentFragment subtitle="Main Development"
                text={project.main}>

                <div className="row">
                    <div className={cx("rowPhotoPortrait", "main2")}></div>
                    <div className={cx("rowPhotoPortrait", "main2")}></div>
                    <div className={cx("rowPhotoLandscape", "main1")}></div>
                </div>
            </ContentFragment>

            <ContentFragment subtitle="Technology"
                text={project.technology}>

                <div className="row">
                    <div className={cx("rowPhotoLandscape", "main1")}></div>
                    <div className={cx("rowPhotoPortrait", "main2")}></div>
                    <div className={cx("rowPhotoPortrait", "main2")}></div>
                </div>
            </ContentFragment>

            <ContentFragment subtitle="Results"
                text={project.results}>

                <div className="row">
                    <div className={cx("rowPhotoLandscape", "main1")}></div>
                    <div className={cx("rowPhotoPortrait", "main2")}></div>
                    <div className={cx("rowPhotoPortrait", "main2")}></div>
                </div>
                {/* <div className="row">
                    <div className={cx("rowPhotoPortrait", "main2")}></div>
                    <div className={cx("rowPhotoPortrait", "main2")}></div>
                    <div className={cx("rowPhotoLandscape", "main1")}></div>
                </div> */}
            </ContentFragment>

            {/* <a href=''>Video1</a> <a href=''>Video2</a> <a href=''>Video3</a> */}

            {project.videos &&
                <ContentFragment subtitle="Videos"
                    text="Some videos related to this project can be found here:">
                    {project.videos.map((link, idx) => {
                        <p key={idx}><a className="link">{link}</a></p>
                    })}
                </ContentFragment>
            }
            <style jsx>{`
                .intro1 {
                    //flex: 0.18;
                    background-image: url('/static/images/projects/rpi/intro1.png');
                }

                .intro2 {
                    //flex: 0.80;
                    background-image: url('/static/images/projects/rpi/intro2.png');
                }

                .main1 {
                    //flex: 0.56;
                    background-image: url('/static/images/projects/rpi/main1.png');
                }

                .main2 {
                    //flex: 0.20;
                    background-image: url('/static/images/projects/rpi/main2.png');
                }

                @media screen and (max-width: 500px) {
                    .intro1, intro2, main1, main2 {
                        //flex:1;
                    }
                }

            `}</style>
        </react.Fragment >
    )
}

export default ProjectRpi;
