import react from 'react'

import tabsStyles from './styles/tabs.module.scss'

class Tabs extends react.Component {
    constructor() {
        super();

        this.state = {
            AcademicActive: true,
            ProfessionalActive: false,
            PersonalActive: false
        }

        this.handleClick = this.handleClick.bind(this);
    }

    render() {
        return (
            <div className={tabsStyles.tabContainer}>
                {this.props.children.map((child, idx) => {
                    return (
                        // <a key={idx} href="#" onClick={() => this.handleClick(child.key)}
                        //     className={this.state[`${child.key}Active`] ? tabsStyles.active : null}>
                        //     {child}
                        // </a>

                        // passing child state to parent
                        <a key={idx} href="#" onClick={() => this.props.onChange(child.key)}>
                            {child}
                        </a>
                    )
                })
                }
            </ div >
        )
    }
}

export default Tabs;


