import react from 'react';
import Link from 'next/link';
import fetch from 'isomorphic-unfetch';
import cx from 'classnames';
import axios from 'axios';

// import layouts
import ContentLayout from '../components/layouts/ContentLayout'
import ContentContainer from '../components/layouts/ContentContainer'

//import components
import ContentHead from '../components/shared/ContentHead'
import BlogsComponent from '../components/blogs/BlogsComponent'

//global styles can be found at ../styles/main.scss
//import specific styling module
import blogsStyles from './styles/blogs.module.scss'

//React Reveal Animation
import Fade from 'react-reveal/Fade';

// import data
import contentHeadData from '../public/static/data/contentHeads.json'
import blogsData from '../public/static/data/blogs.json'

class Blogs extends react.Component {

    static async getInitialProps() {
        // Make request for posts.
        const response = await axios.get('https://asyrulhafetzy.wordpress.com/wp-json/wp/v2/posts')

        console.log(response)
        // Return response to posts object in props.
        return {
            posts: response.data
        }
    }

    renderPosts(posts) {
        return posts.map((post) => {
            return (
                // every iterated react item needs to have a key
                <li key={post.id}>
                    <Link as={`/blogs/${post.id}`} href="/blogs/[id]">
                        <a>{post.title}</a>
                    </Link>
                </li>
            )
        }
        )
    }

    render() {
        const posts = this.props.posts;
        console.log(posts);

        return (
            <ContentLayout>
                <section>
                    <div className={cx('hero', blogsStyles.blogsHero)}></div>

                    <ContentContainer>
                        <Fade delay={200}>
                            <ContentHead data={contentHeadData.blogs} />
                        </Fade>
                    </ContentContainer>
                </section>
                <section>
                    <ContentContainer>
                        <Fade delay={200}>
                            <BlogsComponent data={blogsData.blogs}></BlogsComponent>
                        </Fade>
                        <div className={blogsStyles.loadmoreButton}>Load More</div>
                    </ContentContainer>
                </section>
            </ContentLayout>
        )
    }
}

export default Blogs;

