    render() {
        return (
            <BaseLayout>
                <section className='contentSection'>
                    <div className={cx('hero', aboutStyles.aboutHero)}></div>
                    <div className='contentContainer'>
                        <ContentHead
                            data={
                                {
                                    title: 'Hi, nice to meet you!',
